package basics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// Can be any number, but only whole numbers, though nothing too hardcore.
		int someInteger;
		
		// Basically just an integer, but can be way bigger than an integer.
		long someLong;
		
		// Any number, and can have a decimal value
		double someDouble;
		
		// Any number between and including 0 and 1
		float someFloat;
		
		// Can store a single letter or symbol
		char someChar;
		
		// Can hold a long line of characters, stored between quotes.
		String someString;
		
		// Has a value of true or false.
		boolean someBoolean;
		
		
	}

}
